import React, { useState } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardHeader,
  CardContent,
  Button,
  Box,
  Tabs,
  Tab,
  TextField,
  Paper,
  CircularProgress,
  Divider,
  FormControl,
  InputLabel,
  Input,
  IconButton
} from '@mui/material';
import {
  UploadFile as UploadIcon,
  Link as LinkIcon,
  Close as CloseIcon
} from '@mui/icons-material';
import MatchingResults from './MatchingResults';
import { matchingAPI } from '../../utils/api';

// Кастомные стили
const styles = {
  uploadBox: {
    border: '2px dashed #ccc',
    padding: '20px',
    textAlign: 'center',
    borderRadius: '8px',
    marginBottom: '20px',
    transition: 'all 0.3s',
    '&:hover': {
      borderColor: '#1976d2',
      backgroundColor: '#f0f4ff',
    }
  }
};

const MatchingPage = () => {
  // Состояния для вкладок
  const [resumeTabValue, setResumeTabValue] = useState(0);
  const [vacancyTabValue, setVacancyTabValue] = useState(0);

  // Состояния для данных форм
  const [resumeFile, setResumeFile] = useState(null);
  const [resumeEmail, setResumeEmail] = useState('');
  const [resumeUrl, setResumeUrl] = useState('');
  const [resumeText, setResumeText] = useState('');
  const [vacancyUrl, setVacancyUrl] = useState('');
  const [vacancyText, setVacancyText] = useState('');

  // Состояния для обработки запроса
  const [loading, setLoading] = useState(false);
  const [matchResults, setMatchResults] = useState(null);
  const [error, setError] = useState(null);

  // Обработчики изменения вкладок
  const handleResumeTabChange = (event, newValue) => {
    setResumeTabValue(newValue);
  };

  const handleVacancyTabChange = (event, newValue) => {
    setVacancyTabValue(newValue);
  };

  // Обработчики изменения файла и полей ввода
  const handleResumeFileChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      setResumeFile(event.target.files[0]);
    }
  };

  const handleResumeUrlChange = (event) => {
    setResumeUrl(event.target.value);
  };

  const handleResumeTextChange = (event) => {
    setResumeText(event.target.value);
  };

  const handleResumeEmailChange = (event) => {
    setResumeEmail(event.target.value);
  };

  const handleVacancyUrlChange = (event) => {
    setVacancyUrl(event.target.value);
  };

  const handleVacancyTextChange = (event) => {
    setVacancyText(event.target.value);
  };

  // Валидация форм
  const validateForms = () => {
    setError(null);

    // Валидация формы резюме
    if (resumeTabValue === 0) { // Файл
      if (!resumeFile) {
        setError('Пожалуйста, выберите PDF файл резюме');
        return false;
      }
      if (!resumeEmail || !resumeEmail.includes('@')) {
        setError('Пожалуйста, введите корректный email');
        return false;
      }
    } else if (resumeTabValue === 1) { // URL
      const cleanUrl = resumeUrl.startsWith('@') ? resumeUrl.substring(1) : resumeUrl.trim();
      if (!cleanUrl || !cleanUrl.match(/^https?:\/\/(www\.)?hh\.ru\/resume\/\w+/)) {
        setError('Пожалуйста, введите корректную ссылку на резюме с hh.ru');
        return false;
      }
    } else { // Текст
      if (!resumeText || resumeText.length < 50) {
        setError('Пожалуйста, введите текст резюме (минимум 50 символов)');
        return false;
      }
    }

    // Валидация формы вакансии
    if (vacancyTabValue === 0) { // URL
      const cleanUrl = vacancyUrl.startsWith('@') ? vacancyUrl.substring(1) : vacancyUrl.trim();
      if (!cleanUrl || !cleanUrl.match(/^https?:\/\/(www\.)?hh\.ru\/vacancy\/\d+/)) {
        setError('Пожалуйста, введите корректную ссылку на вакансию с hh.ru');
        return false;
      }
    } else { // Текст
      if (!vacancyText || vacancyText.length < 50) {
        setError('Пожалуйста, введите текст вакансии (минимум 50 символов)');
        return false;
      }
    }

    return true;
  };

  // Обработчик запроса на сопоставление
  const handleMatch = async () => {
    if (!validateForms()) {
      return;
    }

    setLoading(true);
    setMatchResults(null);

    try {
      let response;

      // Определение типа запроса в зависимости от выбранных вкладок
      if (resumeTabValue === 0) { // Файл резюме
        const formData = {
          file: resumeFile,
          email: resumeEmail
        };

        if (vacancyTabValue === 0) { // URL вакансии
          response = await matchingAPI.matchResumeFileToVacancyUrl(formData, vacancyUrl);
        } else { // Текст вакансии
          response = await matchingAPI.matchResumeFileToVacancyText(formData, vacancyText);
        }
      } else if (resumeTabValue === 1) { // URL резюме
        if (vacancyTabValue === 0) { // URL вакансии
          response = await matchingAPI.matchResumeUrlToVacancyUrl(resumeUrl, vacancyUrl);
        } else { // Текст вакансии
          response = await matchingAPI.matchResumeUrlToVacancyText(resumeUrl, vacancyText);
        }
      } else { // Текст резюме
        if (vacancyTabValue === 0) { // URL вакансии
          response = await matchingAPI.matchResumeTextToVacancyUrl(resumeText, vacancyUrl);
        } else { // Текст вакансии
          response = await matchingAPI.matchResumeTextToVacancyText(resumeText, vacancyText);
        }
      }

      // В реальном приложении мы бы использовали данные из response.data
      // Для демонстрации используем заглушку
      await new Promise(resolve => setTimeout(resolve, 2000)); // Имитация задержки

      // Заглушка для результатов сопоставления
      const mockResults = {
        score: Math.random() * 0.7 + 0.3, // Случайная оценка от 0.3 до 1.0
        verdict: 'Кандидат подходит на эту позицию, рекомендуется назначить интервью',
        matched_skills: ['JavaScript', 'React', 'TypeScript', 'HTML', 'CSS'],
        unmatched_skills: ['Vue.js', 'Node.js', 'GraphQL'],
        positives: [
          'Кандидат имеет опыт работы с ключевыми технологиями: React, JavaScript, TypeScript',
          'Опыт работы соответствует требованиям вакансии (3+ года)',
          'Образование в технической сфере соответствует требованиям'
        ],
        negatives: [
          'Отсутствуют некоторые желаемые навыки: Vue.js, GraphQL',
          'Может потребоваться дополнительное обучение для работы с бэкенд-технологиями'
        ],
        llm_comment: 'Кандидат хорошо подходит для данной позиции, имея сильные навыки в требуемых технологиях. Рекомендуется уделить внимание вопросам о его опыте с бэкенд-технологиями во время интервью и проверить готовность к обучению новым фреймворкам.'
      };

      setMatchResults(mockResults);
    } catch (error) {
      console.error('Error during matching:', error);
      setError('Произошла ошибка при выполнении сопоставления. Пожалуйста, попробуйте еще раз.');
    } finally {
      setLoading(false);
    }
  };

  // Обработчик очистки форм
  const handleClearForms = () => {
    setResumeFile(null);
    setResumeEmail('');
    setResumeUrl('');
    setResumeText('');
    setVacancyUrl('');
    setVacancyText('');
    setMatchResults(null);
    setError(null);
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" component="h1" align="center" gutterBottom>
        Сопоставление резюме и вакансий
      </Typography>

      {error && (
        <Paper sx={{ p: 2, mb: 3, bgcolor: '#ffebee' }}>
          <Typography color="error">{error}</Typography>
        </Paper>
      )}

      <Grid container spacing={3}>
        {/* Колонка с резюме */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardHeader
              title="Резюме"
              sx={{ bgcolor: '#1976d2', color: 'white' }}
            />
            <CardContent>
              <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                <Tabs
                  value={resumeTabValue}
                  onChange={handleResumeTabChange}
                  aria-label="resume tabs"
                >
                  <Tab label="Загрузить PDF" />
                  <Tab label="Ссылка с hh.ru" />
                  <Tab label="Текст" />
                </Tabs>
              </Box>

              <Box sx={{ p: 2 }}>
                {resumeTabValue === 0 && (
                  <Box sx={styles.uploadBox}>
                    <Typography>Перетащите PDF файл резюме сюда или</Typography>
                    <FormControl fullWidth sx={{ mb: 2 }}>
                      <Input
                        id="resume-file"
                        type="file"
                        inputProps={{ accept: '.pdf' }}
                        onChange={handleResumeFileChange}
                      />
                    </FormControl>
                    <TextField
                      fullWidth
                      label="Email"
                      placeholder="Введите ваш email"
                      value={resumeEmail}
                      onChange={handleResumeEmailChange}
                      margin="normal"
                    />
                    {resumeFile && (
                      <Box sx={{ mt: 2, display: 'flex', alignItems: 'center' }}>
                        <Typography variant="body2">
                          Выбран файл: {resumeFile.name}
                        </Typography>
                        <IconButton size="small" onClick={() => setResumeFile(null)}>
                          <CloseIcon fontSize="small" />
                        </IconButton>
                      </Box>
                    )}
                  </Box>
                )}

                {resumeTabValue === 1 && (
                  <Box sx={styles.uploadBox}>
                    <Typography>Введите ссылку на резюме с hh.ru</Typography>
                    <TextField
                      fullWidth
                      placeholder="https://hh.ru/resume/..."
                      value={resumeUrl}
                      onChange={handleResumeUrlChange}
                      margin="normal"
                      InputProps={{
                        startAdornment: <LinkIcon color="action" sx={{ mr: 1 }} />,
                      }}
                    />
                  </Box>
                )}

                {resumeTabValue === 2 && (
                  <TextField
                    fullWidth
                    multiline
                    rows={10}
                    placeholder="Вставьте текст резюме"
                    value={resumeText}
                    onChange={handleResumeTextChange}
                    variant="outlined"
                  />
                )}
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {/* Колонка с вакансией */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardHeader
              title="Вакансия"
              sx={{ bgcolor: '#1976d2', color: 'white' }}
            />
            <CardContent>
              <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                <Tabs
                  value={vacancyTabValue}
                  onChange={handleVacancyTabChange}
                  aria-label="vacancy tabs"
                >
                  <Tab label="Ссылка с hh.ru" />
                  <Tab label="Текст" />
                </Tabs>
              </Box>

              <Box sx={{ p: 2 }}>
                {vacancyTabValue === 0 && (
                  <Box sx={styles.uploadBox}>
                    <Typography>Введите ссылку на вакансию с hh.ru</Typography>
                    <TextField
                      fullWidth
                      placeholder="https://hh.ru/vacancy/..."
                      value={vacancyUrl}
                      onChange={handleVacancyUrlChange}
                      margin="normal"
                      InputProps={{
                        startAdornment: <LinkIcon color="action" sx={{ mr: 1 }} />,
                      }}
                    />
                  </Box>
                )}

                {vacancyTabValue === 1 && (
                  <TextField
                    fullWidth
                    multiline
                    rows={10}
                    placeholder="Вставьте текст вакансии"
                    value={vacancyText}
                    onChange={handleVacancyTextChange}
                    variant="outlined"
                  />
                )}
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      <Box sx={{ mt: 3, mb: 5, textAlign: 'center' }}>
        <Button
          variant="contained"
          color="primary"
          size="large"
          onClick={handleMatch}
          disabled={loading}
          sx={{ mr: 2 }}
        >
          {loading ? 'Выполняется сопоставление...' : 'Выполнить сопоставление'}
        </Button>
        <Button
          variant="outlined"
          onClick={handleClearForms}
          disabled={loading}
        >
          Очистить формы
        </Button>
        {loading && (
          <CircularProgress size={24} sx={{ ml: 2 }} />
        )}
      </Box>

      {/* Компонент с результатами сопоставления */}
      {matchResults && <MatchingResults results={matchResults} />}
    </Container>
  );
};

export default MatchingPage;
