import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardHeader,
  CardContent,
  Button,
  Box,
  Tabs,
  Tab,
  TextField,
  Paper,
  CircularProgress,
  FormControl,
  Input,
  IconButton,
  Alert,
  Autocomplete
} from '@mui/material';
import {
  Link as LinkIcon,
  Close as CloseIcon,
  History as HistoryIcon
} from '@mui/icons-material';
import MatchingResults from './MatchingResults';
import { useMatching } from '../../context/MatchingContext';
import { Link } from 'react-router-dom';
import { resumeAPI, vacancyAPI } from '../../utils/api';

// Кастомные стили
const styles = {
  uploadBox: {
    border: '2px dashed #ccc',
    padding: '20px',
    textAlign: 'center',
    borderRadius: '8px',
    marginBottom: '20px',
    transition: 'all 0.3s',
    '&:hover': {
      borderColor: '#1976d2',
      backgroundColor: '#f0f4ff',
    }
  }
};

const MatchingPage = () => {
  // Состояния для вкладок
  const [resumeTabValue, setResumeTabValue] = useState(0);
  const [vacancyTabValue, setVacancyTabValue] = useState(0);

  // Состояния для данных форм
  const [resumeFile, setResumeFile] = useState(null);
  const [resumeEmail, setResumeEmail] = useState('');
  const [resumeUrl, setResumeUrl] = useState('');
  const [resumeText, setResumeText] = useState('');
  const [vacancyUrl, setVacancyUrl] = useState('');
  const [vacancyText, setVacancyText] = useState('');

  // Новые состояния для списков и выбранных элементов
  const [resumes, setResumes] = useState([]);
  const [vacancies, setVacancies] = useState([]);
  const [selectedResume, setSelectedResume] = useState(null);
  const [selectedVacancy, setSelectedVacancy] = useState(null);
  const [matchingResult, setMatchingResult] = useState(null);
  const [loadingDropdowns, setLoadingDropdowns] = useState(false);

  // Получение состояния и функций из контекста
  const { currentMatch, loading, error: matchingError, performMatching } = useMatching();

  // Локальная ошибка (для валидации форм)
  const [error, setError] = useState(null);

  // Загрузка резюме и вакансий при монтировании
  useEffect(() => {
    setLoadingDropdowns(true);
    Promise.all([
      resumeAPI.getAll(),
      vacancyAPI.getAll()
    ]).then(([resumesRes, vacanciesRes]) => {
      setResumes(resumesRes.data || []);
      setVacancies(vacanciesRes.data || []);
    }).catch(() => {
      setError('Ошибка загрузки резюме или вакансий');
    }).finally(() => setLoadingDropdowns(false));
  }, []);

  // Обработчики изменения вкладок
  const handleResumeTabChange = (event, newValue) => {
    setResumeTabValue(newValue);
  };

  const handleVacancyTabChange = (event, newValue) => {
    setVacancyTabValue(newValue);
  };

  // Обработчики изменения файла и полей ввода
  const handleResumeFileChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      setResumeFile(event.target.files[0]);
    }
  };

  const handleResumeUrlChange = (event) => {
    setResumeUrl(event.target.value);
  };

  const handleResumeTextChange = (event) => {
    setResumeText(event.target.value);
  };

  const handleResumeEmailChange = (event) => {
    setResumeEmail(event.target.value);
  };

  const handleVacancyUrlChange = (event) => {
    setVacancyUrl(event.target.value);
  };

  const handleVacancyTextChange = (event) => {
    setVacancyText(event.target.value);
  };

  // Валидация форм
  const validateForms = () => {
    setError(null);

    // Валидация формы резюме
    if (resumeTabValue === 0) { // Файл
      if (!resumeFile) {
        setError('Пожалуйста, выберите PDF файл резюме');
        return false;
      }
      if (!resumeEmail || !resumeEmail.includes('@')) {
        setError('Пожалуйста, введите корректный email');
        return false;
      }
    } else if (resumeTabValue === 1) { // URL
      const cleanUrl = resumeUrl.startsWith('@') ? resumeUrl.substring(1) : resumeUrl.trim();
      if (!cleanUrl || !cleanUrl.match(/^https?:\/\/(www\.)?hh\.ru\/resume\/\w+/)) {
        setError('Пожалуйста, введите корректную ссылку на резюме с hh.ru');
        return false;
      }
    } else { // Текст
      if (!resumeText || resumeText.length < 50) {
        setError('Пожалуйста, введите текст резюме (минимум 50 символов)');
        return false;
      }
    }

    // Валидация формы вакансии
    if (vacancyTabValue === 0) { // URL
      const cleanUrl = vacancyUrl.startsWith('@') ? vacancyUrl.substring(1) : vacancyUrl.trim();
      if (!cleanUrl || !cleanUrl.match(/^https?:\/\/(www\.)?hh\.ru\/vacancy\/\d+/)) {
        setError('Пожалуйста, введите корректную ссылку на вакансию с hh.ru');
        return false;
      }
    } else { // Текст
      if (!vacancyText || vacancyText.length < 50) {
        setError('Пожалуйста, введите текст вакансии (минимум 50 символов)');
        return false;
      }
    }

    return true;
  };

  // Обработчик запроса на сопоставление
  const handleMatch = async () => {
    if (!validateForms()) {
      return;
    }

    try {
      let resumeType, resumeData, vacancyType, vacancyData;

      // Определение типа резюме и данных
      if (resumeTabValue === 0) { // Файл
        resumeType = 'file';
        resumeData = {
          file: resumeFile,
          email: resumeEmail
        };
      } else if (resumeTabValue === 1) { // URL
        resumeType = 'url';
        resumeData = resumeUrl;
      } else { // Текст
        resumeType = 'text';
        resumeData = resumeText;
      }

      // Определение типа вакансии и данных
      if (vacancyTabValue === 0) { // URL
        vacancyType = 'url';
        vacancyData = vacancyUrl;
      } else { // Текст
        vacancyType = 'text';
        vacancyData = vacancyText;
      }

      // Вызов функции из контекста для выполнения сопоставления
      await performMatching(resumeType, resumeData, vacancyType, vacancyData);
    } catch (error) {
      console.error('Error during matching:', error);
      setError('Произошла ошибка при выполнении сопоставления. Пожалуйста, попробуйте еще раз.');
    }
  };

  // Обработчик очистки форм
  const handleClearForms = () => {
    setResumeFile(null);
    setResumeEmail('');
    setResumeUrl('');
    setResumeText('');
    setVacancyUrl('');
    setVacancyText('');
    setError(null);
  };

  // Новый обработчик сопоставления по id
  const handleIdMatch = async () => {
    if (!selectedResume || !selectedVacancy) {
      setError('Пожалуйста, выберите резюме и вакансию');
      return;
    }
    setError(null);
    setMatchingResult(null);
    try {
      const response = await fetch('http://localhost:8081/api/resume-vacancy-matches/full', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...(localStorage.getItem('hr_partner_auth') ? {
            'Authorization': `Bearer ${JSON.parse(localStorage.getItem('hr_partner_auth')).token}`
          } : {})
        },
        body: JSON.stringify({
          resumeId: selectedResume.id,
          vacancyId: selectedVacancy.id
        })
      });
      if (!response.ok) throw new Error('Ошибка сопоставления');
      const data = await response.json();
      setMatchingResult(data);
    } catch (e) {
      setError('Ошибка сопоставления: ' + e.message);
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h1">
          Сопоставление резюме и вакансий
        </Typography>

        <Button
          component={Link}
          to="/matching/history"
          variant="outlined"
          startIcon={<HistoryIcon />}
        >
          История сопоставлений
        </Button>
      </Box>

      {error && (
        <Paper sx={{ p: 2, mb: 3, bgcolor: '#ffebee' }}>
          <Typography color="error">{error}</Typography>
        </Paper>
      )}

      {matchingError && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {matchingError}
        </Alert>
      )}

      <Grid container spacing={3}>
        {/* Колонка с резюме */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardHeader
              title="Резюме"
              sx={{ bgcolor: '#1976d2', color: 'white' }}
            />
            <CardContent>
              <Box sx={{ mb: 2 }}>
                <Autocomplete
                  options={resumes}
                  getOptionLabel={(option) => `${option.name} (${option.role || 'Без роли'})`}
                  loading={loadingDropdowns}
                  value={selectedResume}
                  onChange={(_, value) => setSelectedResume(value)}
                  renderInput={(params) => (
                    <TextField {...params} label="Выберите резюме" placeholder="Начните вводить имя..." />
                  )}
                  isOptionEqualToValue={(option, value) => option.id === value.id}
                />
              </Box>
              <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                <Tabs
                  value={resumeTabValue}
                  onChange={handleResumeTabChange}
                  aria-label="resume tabs"
                >
                  <Tab label="Загрузить PDF" />
                  <Tab label="Ссылка с hh.ru" />
                  <Tab label="Текст" />
                </Tabs>
              </Box>

              <Box sx={{ p: 2 }}>
                {resumeTabValue === 0 && (
                  <Box sx={styles.uploadBox}>
                    <Typography>Перетащите PDF файл резюме сюда или</Typography>
                    <FormControl fullWidth sx={{ mb: 2 }}>
                      <Input
                        id="resume-file"
                        type="file"
                        inputProps={{ accept: '.pdf' }}
                        onChange={handleResumeFileChange}
                      />
                    </FormControl>
                    <TextField
                      fullWidth
                      label="Email"
                      placeholder="Введите ваш email"
                      value={resumeEmail}
                      onChange={handleResumeEmailChange}
                      margin="normal"
                    />
                    {resumeFile && (
                      <Box sx={{ mt: 2, display: 'flex', alignItems: 'center' }}>
                        <Typography variant="body2">
                          Выбран файл: {resumeFile.name}
                        </Typography>
                        <IconButton size="small" onClick={() => setResumeFile(null)}>
                          <CloseIcon fontSize="small" />
                        </IconButton>
                      </Box>
                    )}
                  </Box>
                )}

                {resumeTabValue === 1 && (
                  <Box sx={styles.uploadBox}>
                    <Typography>Введите ссылку на резюме с hh.ru</Typography>
                    <TextField
                      fullWidth
                      placeholder="https://hh.ru/resume/..."
                      value={resumeUrl}
                      onChange={handleResumeUrlChange}
                      margin="normal"
                      InputProps={{
                        startAdornment: <LinkIcon color="action" sx={{ mr: 1 }} />,
                      }}
                    />
                  </Box>
                )}

                {resumeTabValue === 2 && (
                  <TextField
                    fullWidth
                    multiline
                    rows={10}
                    placeholder="Вставьте текст резюме"
                    value={resumeText}
                    onChange={handleResumeTextChange}
                    variant="outlined"
                  />
                )}
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {/* Колонка с вакансией */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardHeader
              title="Вакансия"
              sx={{ bgcolor: '#1976d2', color: 'white' }}
            />
            <CardContent>
              <Box sx={{ mb: 2 }}>
                <Autocomplete
                  options={vacancies}
                  getOptionLabel={(option) => `${option.title} (${option.company || 'Без компании'})`}
                  loading={loadingDropdowns}
                  value={selectedVacancy}
                  onChange={(_, value) => setSelectedVacancy(value)}
                  renderInput={(params) => (
                    <TextField {...params} label="Выберите вакансию" placeholder="Начните вводить название..." />
                  )}
                  isOptionEqualToValue={(option, value) => option.id === value.id}
                />
              </Box>
              <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                <Tabs
                  value={vacancyTabValue}
                  onChange={handleVacancyTabChange}
                  aria-label="vacancy tabs"
                >
                  <Tab label="Ссылка с hh.ru" />
                  <Tab label="Текст" />
                </Tabs>
              </Box>

              <Box sx={{ p: 2 }}>
                {vacancyTabValue === 0 && (
                  <Box sx={styles.uploadBox}>
                    <Typography>Введите ссылку на вакансию с hh.ru</Typography>
                    <TextField
                      fullWidth
                      placeholder="https://hh.ru/vacancy/..."
                      value={vacancyUrl}
                      onChange={handleVacancyUrlChange}
                      margin="normal"
                      InputProps={{
                        startAdornment: <LinkIcon color="action" sx={{ mr: 1 }} />,
                      }}
                    />
                  </Box>
                )}

                {vacancyTabValue === 1 && (
                  <TextField
                    fullWidth
                    multiline
                    rows={10}
                    placeholder="Вставьте текст вакансии"
                    value={vacancyText}
                    onChange={handleVacancyTextChange}
                    variant="outlined"
                  />
                )}
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      <Box sx={{ mt: 3, mb: 5, textAlign: 'center' }}>
        <Button
          variant="contained"
          color="primary"
          size="large"
          onClick={handleIdMatch}
          disabled={loadingDropdowns || loading}
          sx={{ mr: 2 }}
        >
          {loading ? 'Выполняется сопоставление...' : 'Выполнить сопоставление'}
        </Button>
        <Button
          variant="outlined"
          onClick={handleClearForms}
          disabled={loadingDropdowns || loading}
        >
          Очистить формы
        </Button>
        {loading && (
          <CircularProgress size={24} sx={{ ml: 2 }} />
        )}
      </Box>

      {/* Компонент с результатами сопоставления */}
      {matchingResult && <MatchingResults results={matchingResult} />}
    </Container>
  );
};

export default MatchingPage;
