import React, { useState } from 'react';
import { 
  Card, 
  CardContent, 
  Typography, 
  Chip, 
  Box, 
  IconButton,
  Menu,
  MenuItem,
  ListItemIcon,
  ListItemText,
  Divider,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Button,
  Avatar,
  Rating,
  Link
} from '@mui/material';
import { 
  LocationOn as LocationIcon,
  School as EducationIcon,
  MoreVert as MoreIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Description as ResumeIcon,
  Email as EmailIcon,
  Phone as PhoneIcon,
  Send as SendIcon,
  EventNote as InterviewIcon,
  Check as ApproveIcon,
  Close as RejectIcon,
  Person as PersonIcon
} from '@mui/icons-material';
import { format } from 'date-fns';
import { ru } from 'date-fns/locale';

const statusNames = {
  new: 'Новый',
  reviewing: 'На рассмотрении',
  interviewing: 'На собеседовании',
  offer: 'Предложение',
  rejected: 'Отклонен'
};

const statusColors = {
  new: '#2196f3', // синий
  reviewing: '#ff9800', // оранжевый
  interviewing: '#673ab7', // фиолетовый
  offer: '#4caf50', // зеленый
  rejected: '#f44336' // красный
};

const CandidateCard = ({ candidate, onEdit, onDelete, onStatusChange }) => {
  const [anchorEl, setAnchorEl] = useState(null);
  const [statusAnchorEl, setStatusAnchorEl] = useState(null);
  const [deleteConfirmOpen, setDeleteConfirmOpen] = useState(false);
  const [showAllAchievements, setShowAllAchievements] = useState({});

  // Форматирование даты
  const formattedDate = React.useMemo(() => {
    try {
      return format(new Date(candidate.createdAt), 'dd MMMM yyyy', { locale: ru });
    } catch (error) {
      return 'Дата не указана';
    }
  }, [candidate.createdAt]);

  // Обработчики для меню действий
  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  // Обработчики для меню статуса
  const handleStatusMenuOpen = (event) => {
    setStatusAnchorEl(event.currentTarget);
    handleMenuClose();
  };

  const handleStatusMenuClose = () => {
    setStatusAnchorEl(null);
  };

  // Обработчики действий
  const handleEdit = () => {
    handleMenuClose();
    if (onEdit) onEdit(candidate);
  };

  const handleDeleteConfirm = () => {
    setDeleteConfirmOpen(true);
    handleMenuClose();
  };

  const handleDeleteCancel = () => {
    setDeleteConfirmOpen(false);
  };

  const handleDeleteConfirmed = () => {
    setDeleteConfirmOpen(false);
    if (onDelete) onDelete(candidate.id);
  };

  const handleStatusChange = (newStatus) => {
    handleStatusMenuClose();
    if (onStatusChange) onStatusChange(candidate.id, newStatus);
  };

  const handleViewResume = () => {
    window.open(candidate.resumeUrl, '_blank');
    handleMenuClose();
  };

  // Получение инициалов из имени
  const getInitials = (name) => {
    return name
      .split(' ')
      .map(part => part.charAt(0))
      .join('')
      .toUpperCase();
  };

  // Функция для форматирования образования
  const formatEducation = (education) => {
    if (Array.isArray(education)) {
      return education.map((edu, idx) =>
        [edu.degree, edu.direction, edu.specialty].filter(Boolean).join(', ')
      ).join('; ');
    }
    return education || 'Не указано';
  };

  // Функция для форматирования периода работы
  const formatPeriod = (start, end) => {
    return `${start || ''}${start && end ? ' — ' : ''}${end || ''}`;
  };

  const handleToggleAchievements = (idx) => {
    setShowAllAchievements((prev) => ({ ...prev, [idx]: !prev[idx] }));
  };

  return (
    <>
      <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
        <CardContent sx={{ flexGrow: 1 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <Avatar 
                sx={{ 
                  bgcolor: statusColors[candidate.status] || 'primary.main',
                  mr: 2
                }}
              >
                {getInitials(candidate.name)}
              </Avatar>
              <Box>
                <Typography variant="h6" component="h2">
                  {candidate.name}
                </Typography>
                <Typography variant="subtitle1" color="text.secondary">
                  {candidate.position}
                </Typography>
              </Box>
            </Box>

            <IconButton 
              size="small" 
              onClick={handleMenuOpen} 
              aria-label="Действия"
              aria-controls="candidate-menu"
              aria-haspopup="true"
            >
              <MoreIcon />
            </IconButton>
          </Box>

          <Divider sx={{ mb: 2 }} />

          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1, mb: 2 }}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <EducationIcon fontSize="small" sx={{ mr: 1, color: 'text.secondary' }} />
              <Typography variant="body2" color="text.secondary">
                {formatEducation(candidate.education)}
              </Typography>
            </Box>
          </Box>

          <Box sx={{ mb: 2 }}>
            <Typography variant="subtitle1" sx={{ fontWeight: 'bold', mb: 1 }}>
              Опыт работы
            </Typography>
            {Array.isArray(candidate.workExperience) && candidate.workExperience.length > 0 ? (
              candidate.workExperience.map((exp, idx) => (
                <Box key={idx} sx={{ mb: 2, pl: 1, borderLeft: '2px solid #eee' }}>
                  <Typography variant="body2" sx={{ fontWeight: 500 }}>
                    {exp.company_name} <span style={{ color: '#888', fontWeight: 400 }}>({formatPeriod(exp.start_date, exp.end_date)})</span>
                  </Typography>
                  {exp.role && (
                    <Typography variant="body2" sx={{ color: 'text.secondary', mb: 0.5 }}>
                      {exp.role}
                    </Typography>
                  )}
                  {exp.technologies && exp.technologies.length > 0 && (
                    <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mb: 0.5 }}>
                      Технологии: {exp.technologies.join(', ')}
                    </Typography>
                  )}
                  {exp.achievements && exp.achievements.length > 0 && (
                    <Box>
                      {(showAllAchievements[idx] ? exp.achievements : exp.achievements.slice(0, 2)).map((ach, i) => (
                        <Typography key={i} variant="body2" sx={{ fontSize: '0.95em', color: 'text.secondary', pl: 1, mb: 0.5 }}>
                          • {ach}
                        </Typography>
                      ))}
                      {exp.achievements.length > 2 && (
                        <Button size="small" onClick={() => handleToggleAchievements(idx)} sx={{ pl: 1 }}>
                          {showAllAchievements[idx] ? 'Скрыть' : `Показать все (${exp.achievements.length})`}
                        </Button>
                      )}
                    </Box>
                  )}
                </Box>
              ))
            ) : (
              <Typography variant="body2" color="text.secondary">{candidate.experience || 'Не указан'}</Typography>
            )}
          </Box>

          <Box sx={{ mb: 2 }}>
            <Typography variant="body2" sx={{ mb: 0.5 }}>
              <strong>Ожидания по ЗП:</strong> {candidate.salary}
            </Typography>
          </Box>

          <Typography variant="body2" sx={{ mb: 2 }} color="text.secondary" noWrap>
            {candidate.about}
          </Typography>

          <Box sx={{ mb: 2, display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
            {[...(candidate.hardSkills || []), ...(candidate.softSkills || []), ...(candidate.skills || [])].map((skill, index) => (
              <Chip
                key={index}
                label={skill}
                size="small"
                sx={{ mr: 0.5, mb: 0.5 }}
              />
            ))}
            {(!candidate.hardSkills && !candidate.softSkills && (!candidate.skills || candidate.skills.length === 0)) && (
              <Typography variant="body2" color="text.secondary">
                Навыки не указаны
              </Typography>
            )}
          </Box>

          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mt: 'auto' }}>
            <Typography variant="caption" color="text.secondary">
              Добавлен: {formattedDate}
            </Typography>

            <Chip 
              label={candidate.candidateStatus?.title || statusNames[candidate.status] || 'Неизвестно'} 
              size="small"
              sx={{
                bgcolor: `${statusColors[candidate.status]}20`,
                color: statusColors[candidate.status],
                fontWeight: 'bold'
              }}
            />
          </Box>

          <Box sx={{ display: 'flex', mt: 2, gap: 1 }}>
            <Button 
              size="small" 
              startIcon={<EmailIcon />} 
              variant="outlined" 
              sx={{ flex: 1 }}
              component={Link}
              href={`mailto:${candidate.email}`}
            >
              Email
            </Button>
            <Button 
              size="small" 
              startIcon={<PhoneIcon />} 
              variant="outlined" 
              sx={{ flex: 1 }}
              component={Link}
              href={`tel:${candidate.phone}`}
            >
              Позвонить
            </Button>
          </Box>
        </CardContent>
      </Card>

      {/* Меню действий */}
      <Menu
        id="candidate-menu"
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleMenuClose}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'right',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'right',
        }}
      >
        <MenuItem onClick={handleViewResume}>
          <ListItemIcon>
            <ResumeIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText>Просмотреть резюме</ListItemText>
        </MenuItem>
        <MenuItem onClick={handleEdit}>
          <ListItemIcon>
            <EditIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText>Редактировать</ListItemText>
        </MenuItem>
        <MenuItem onClick={handleStatusMenuOpen}>
          <ListItemIcon>
            <PersonIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText>Изменить статус</ListItemText>
        </MenuItem>
        <Divider />
        <MenuItem onClick={handleDeleteConfirm} sx={{ color: 'error.main' }}>
          <ListItemIcon>
            <DeleteIcon fontSize="small" color="error" />
          </ListItemIcon>
          <ListItemText>Удалить</ListItemText>
        </MenuItem>
      </Menu>

      {/* Меню статусов */}
      <Menu
        id="status-menu"
        anchorEl={statusAnchorEl}
        open={Boolean(statusAnchorEl)}
        onClose={handleStatusMenuClose}
        anchorOrigin={{
          vertical: 'top',
          horizontal: 'right',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'left',
        }}
      >
        <MenuItem onClick={() => handleStatusChange('new')}>
          <ListItemIcon>
            <PersonIcon fontSize="small" style={{ color: statusColors.new }} />
          </ListItemIcon>
          <ListItemText>Новый</ListItemText>
        </MenuItem>
        <MenuItem onClick={() => handleStatusChange('reviewing')}>
          <ListItemIcon>
            <ResumeIcon fontSize="small" style={{ color: statusColors.reviewing }} />
          </ListItemIcon>
          <ListItemText>На рассмотрении</ListItemText>
        </MenuItem>
        <MenuItem onClick={() => handleStatusChange('interviewing')}>
          <ListItemIcon>
            <InterviewIcon fontSize="small" style={{ color: statusColors.interviewing }} />
          </ListItemIcon>
          <ListItemText>На собеседовании</ListItemText>
        </MenuItem>
        <MenuItem onClick={() => handleStatusChange('offer')}>
          <ListItemIcon>
            <SendIcon fontSize="small" style={{ color: statusColors.offer }} />
          </ListItemIcon>
          <ListItemText>Предложение</ListItemText>
        </MenuItem>
        <MenuItem onClick={() => handleStatusChange('rejected')}>
          <ListItemIcon>
            <RejectIcon fontSize="small" style={{ color: statusColors.rejected }} />
          </ListItemIcon>
          <ListItemText>Отклонен</ListItemText>
        </MenuItem>
      </Menu>

      {/* Диалог подтверждения удаления */}
      <Dialog
        open={deleteConfirmOpen}
        onClose={handleDeleteCancel}
      >
        <DialogTitle>Удаление кандидата</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Вы уверены, что хотите удалить кандидата "{candidate.name}"? Это действие нельзя будет отменить.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDeleteCancel} color="primary">
            Отмена
          </Button>
          <Button onClick={handleDeleteConfirmed} color="error" autoFocus>
            Удалить
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
};

export default CandidateCard;
