import React, { useState } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  Tabs,
  Tab,
  Box,
  Typography,
  Paper,
  IconButton,
  CircularProgress,
  Alert,
  Snackbar,
  Divider
} from '@mui/material';
import {
  ContentPaste as PasteIcon,
  Link as LinkIcon,
  Close as CloseIcon,
  Upload as UploadIcon
} from '@mui/icons-material';

// Примеры форматов импорта
const sampleFormats = {
  text: `Название: Frontend-разработчик
Компания: Tech Solutions
Местоположение: Москва
Зарплата: 120 000 - 180 000 ₽
Опыт работы: 3-5 лет
Описание: Мы ищем опытного Frontend-разработчика со знанием React и TypeScript...
Навыки: React, JavaScript, TypeScript, CSS, HTML
`,

  link: 'https://example.com/job/frontend-developer'
};

const VacancyImport = ({ open, onClose, onImport }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [textContent, setTextContent] = useState('');
  const [urlLink, setUrlLink] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  // Обработчик изменения вкладки
  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
    setError(null);
  };

  // Обработчик изменения текстового содержимого
  const handleTextChange = (e) => {
    setTextContent(e.target.value);
    if (error) setError(null);
  };

  // Обработчик изменения URL
  const handleUrlChange = (e) => {
    setUrlLink(e.target.value);
    if (error) setError(null);
  };

  // Обработчик вставки примера
  const handlePasteSample = () => {
    if (activeTab === 0) {
      setTextContent(sampleFormats.text);
    } else {
      setUrlLink(sampleFormats.link);
    }
  };

  // Обработчик импорта
  const handleImport = async () => {
    setLoading(true);
    setError(null);

    try {
      if (activeTab === 0) { // Импорт из текста
        if (!textContent.trim()) {
          throw new Error('Пожалуйста, введите текст для импорта');
        }

        // Парсинг текста
        const parsedVacancy = parseTextToVacancy(textContent);

        // Имитация задержки запроса
        await new Promise(resolve => setTimeout(resolve, 1000));

        if (onImport) {
          onImport(parsedVacancy);
        }

        setSnackbarOpen(true);
        handleClose();
      } else { // Импорт из ссылки
        if (!urlLink.trim()) {
          throw new Error('Пожалуйста, введите ссылку для импорта');
        }

        if (!urlLink.startsWith('http')) {
          throw new Error('Пожалуйста, введите корректную ссылку, начинающуюся с http:// или https://');
        }

        // Имитация задержки запроса
        await new Promise(resolve => setTimeout(resolve, 1000));

        // Заглушка для импорта из ссылки (в реальном приложении здесь будет запрос к API)
        const mockVacancy = {
          title: 'Импортированная вакансия',
          company: 'Компания из ссылки',
          location: 'Удаленно',
          salary: '100 000 - 150 000 ₽',
          experience: '1-3 года',
          description: 'Эта вакансия была импортирована по ссылке: ' + urlLink,
          skills: ['Imported', 'From', 'Link'],
          status: 'active'
        };

        if (onImport) {
          onImport(mockVacancy);
        }

        setSnackbarOpen(true);
        handleClose();
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Функция для парсинга текста в структуру вакансии
  const parseTextToVacancy = (text) => {
    const lines = text.split('\n');
    const vacancy = {
      status: 'active',
      skills: []
    };

    lines.forEach(line => {
      const [key, value] = line.split(':').map(item => item.trim());
      if (!key || !value) return;

      switch (key.toLowerCase()) {
        case 'название':
          vacancy.title = value;
          break;
        case 'компания':
          vacancy.company = value;
          break;
        case 'местоположение':
          vacancy.location = value;
          break;
        case 'зарплата':
          vacancy.salary = value;
          break;
        case 'опыт работы':
          vacancy.experience = value;
          break;
        case 'описание':
          vacancy.description = value;
          break;
        case 'навыки':
          vacancy.skills = value.split(',').map(skill => skill.trim());
          break;
        case 'статус':
          vacancy.status = value.toLowerCase() === 'активная' ? 'active' : 'closed';
          break;
        default:
          break;
      }
    });

    // Проверка обязательных полей
    if (!vacancy.title) {
      throw new Error('Не указано название вакансии');
    }
    if (!vacancy.company) {
      throw new Error('Не указано название компании');
    }
    if (!vacancy.description) {
      throw new Error('Не указано описание вакансии');
    }

    return vacancy;
  };

  // Обработчик закрытия диалога
  const handleClose = () => {
    setActiveTab(0);
    setTextContent('');
    setUrlLink('');
    setError(null);
    onClose();
  };

  // Обработчик закрытия снэкбара
  const handleSnackbarClose = () => {
    setSnackbarOpen(false);
  };

  return (
    <>
      <Dialog 
        open={open} 
        onClose={handleClose}
        fullWidth
        maxWidth="md"
      >
        <DialogTitle>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            Импортировать вакансию
            <IconButton edge="end" onClick={handleClose}>
              <CloseIcon />
            </IconButton>
          </Box>
        </DialogTitle>
        <Divider />

        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
          <Tabs 
            value={activeTab} 
            onChange={handleTabChange} 
            variant="fullWidth"
            centered
          >
            <Tab label="Импорт из текста" />
            <Tab label="Импорт по ссылке" />
          </Tabs>
        </Box>

        <DialogContent>
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}

          {activeTab === 0 ? (
            <Box>
              <Typography variant="body2" sx={{ mb: 2 }}>
                Вставьте текст вакансии в формате "Ключ: Значение" для каждой строки. Поддерживаемые ключи: Название, Компания, Местоположение, Зарплата, Опыт работы, Описание, Навыки.
              </Typography>

              <TextField
                fullWidth
                multiline
                rows={10}
                placeholder="Введите текст вакансии..."
                value={textContent}
                onChange={handleTextChange}
                variant="outlined"
                disabled={loading}
              />

              <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 1 }}>
                <Button 
                  startIcon={<PasteIcon />} 
                  size="small" 
                  onClick={handlePasteSample}
                  disabled={loading}
                >
                  Вставить пример
                </Button>
              </Box>
            </Box>
          ) : (
            <Box>
              <Typography variant="body2" sx={{ mb: 2 }}>
                Вставьте ссылку на вакансию, система попытается автоматически извлечь информацию.
              </Typography>

              <TextField
                fullWidth
                placeholder="https://example.com/job/123"
                value={urlLink}
                onChange={handleUrlChange}
                variant="outlined"
                disabled={loading}
                InputProps={{
                  startAdornment: <LinkIcon color="action" sx={{ mr: 1 }} />,
                }}
              />

              <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 1 }}>
                <Button 
                  startIcon={<PasteIcon />} 
                  size="small" 
                  onClick={handlePasteSample}
                  disabled={loading}
                >
                  Вставить пример
                </Button>
              </Box>

              <Paper elevation={0} sx={{ p: 2, bgcolor: 'grey.100', mt: 2 }}>
                <Typography variant="body2">
                  Поддерживаемые платформы для импорта:
                </Typography>
                <ul>
                  <li>HeadHunter</li>
                  <li>SuperJob</li>
                  <li>LinkedIn</li>
                  <li>Habr Career</li>
                </ul>
                <Typography variant="caption" color="text.secondary">
                  Примечание: Импорт может работать не для всех вакансий на указанных платформах.
                </Typography>
              </Paper>
            </Box>
          )}
        </DialogContent>

        <DialogActions sx={{ px: 3, pb: 3 }}>
          <Button onClick={handleClose} color="inherit" disabled={loading}>
            Отмена
          </Button>
          <Button
            startIcon={loading ? <CircularProgress size={24} /> : <UploadIcon />}
            onClick={handleImport}
            variant="contained"
            color="primary"
            disabled={loading}
          >
            {loading ? 'Импортирую...' : 'Импортировать'}
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar
        open={snackbarOpen}
        autoHideDuration={4000}
        onClose={handleSnackbarClose}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }}
      >
        <Alert onClose={handleSnackbarClose} severity="success" sx={{ width: '100%' }}>
          Вакансия успешно импортирована
        </Alert>
      </Snackbar>
    </>
  );
};

export default VacancyImport;
