import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Paper,
  Button,
  Grid,
  Tabs,
  Tab,
  Divider,
  TextField,
  InputAdornment,
  Fab,
  Snackbar,
  Alert,
  Menu,
  MenuItem,
  ListItemIcon,
  ListItemText
} from '@mui/material';
import { 
  Add as AddIcon, 
  Search as SearchIcon,
  FilterList as FilterIcon,
  CloudUpload as ImportIcon,
  TextSnippet as TextIcon,
  UploadFile as FileIcon
} from '@mui/icons-material';
import CandidateCard from './CandidateCard';
import CandidateForm from './CandidateForm';
import CandidateImport from './CandidateImport';

const CandidateList = () => {
  const [tabValue, setTabValue] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');
  const [candidates, setCandidates] = useState([]);
  const [filteredCandidates, setFilteredCandidates] = useState([]);
  const [formDialogOpen, setFormDialogOpen] = useState(false);
  const [importDialogOpen, setImportDialogOpen] = useState(false);
  const [currentCandidate, setCurrentCandidate] = useState(null);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success'
  });
  const [importMenuAnchorEl, setImportMenuAnchorEl] = useState(null);

  // Обработчик изменения вкладки
  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
    filterCandidates(searchQuery, newValue);
  };

  // Обработчик поиска
  const handleSearch = (event) => {
    const query = event.target.value;
    setSearchQuery(query);
    filterCandidates(query, tabValue);
  };

  // Фильтрация кандидатов
  const filterCandidates = (query, tab) => {
    let statusFilter = '';
    if (tab === 1) statusFilter = 'new';
    if (tab === 2) statusFilter = 'reviewing';
    if (tab === 3) statusFilter = 'interviewing';
    if (tab === 4) statusFilter = 'offer';
    if (tab === 5) statusFilter = 'rejected';

    setFilteredCandidates(
      candidates.filter(candidate => 
        (statusFilter ? candidate.status === statusFilter : true) && 
        (candidate.name.toLowerCase().includes(query.toLowerCase()) ||
         candidate.position.toLowerCase().includes(query.toLowerCase()) ||
         candidate.skills.some(skill => skill.toLowerCase().includes(query.toLowerCase())))
      )
    );
  };

  // Обработчик открытия формы
  const handleOpenForm = (candidate = null) => {
    setCurrentCandidate(candidate);
    setFormDialogOpen(true);
  };

  // Обработчик закрытия формы
  const handleCloseForm = (formData) => {
    setFormDialogOpen(false);

    // Если пользователь отправил форму с данными
    if (formData && formData !== true) {
      if (currentCandidate) {
        // Редактирование существующего кандидата
        const updatedCandidates = candidates.map(cand => 
          cand.id === currentCandidate.id ? { ...formData, id: currentCandidate.id, createdAt: currentCandidate.createdAt } : cand
        );
        setCandidates(updatedCandidates);
        filterCandidates(searchQuery, tabValue);

        showSnackbar('Данные кандидата успешно обновлены', 'success');
      } else {
        // Добавление нового кандидата
        const newCandidate = {
          ...formData,
          id: Date.now(), // Временный способ генерации ID
          createdAt: new Date().toISOString()
        };
        const updatedCandidates = [...candidates, newCandidate];
        setCandidates(updatedCandidates);
        filterCandidates(searchQuery, tabValue);

        showSnackbar('Кандидат успешно добавлен', 'success');
      }
    }

    setCurrentCandidate(null);
  };

  // Обработчик удаления кандидата
  const handleDeleteCandidate = (id) => {
    const updatedCandidates = candidates.filter(candidate => candidate.id !== id);
    setCandidates(updatedCandidates);
    filterCandidates(searchQuery, tabValue);

    showSnackbar('Кандидат удален', 'success');
  };

  // Обработчик изменения статуса кандидата
  const handleStatusChange = (id, newStatus) => {
    const updatedCandidates = candidates.map(candidate => 
      candidate.id === id ? { ...candidate, status: newStatus } : candidate
    );

    setCandidates(updatedCandidates);
    filterCandidates(searchQuery, tabValue);

    const statusMessages = {
      'new': 'Статус изменен на "Новый"',
      'reviewing': 'Статус изменен на "На рассмотрении"',
      'interviewing': 'Статус изменен на "На собеседовании"',
      'offer': 'Статус изменен на "Предложение"',
      'rejected': 'Статус изменен на "Отклонен"'
    };

    showSnackbar(statusMessages[newStatus] || 'Статус кандидата изменен', 'success');
  };

  // Обработчики импорта
  const handleImportMenuOpen = (event) => {
    setImportMenuAnchorEl(event.currentTarget);
  };

  const handleImportMenuClose = () => {
    setImportMenuAnchorEl(null);
  };

  const handleOpenImportDialog = () => {
    handleImportMenuClose();
    setImportDialogOpen(true);
  };

  const handleCloseImportDialog = () => {
    setImportDialogOpen(false);
  };

  const handleImportCandidate = (importedCandidate) => {
    const newCandidate = {
      ...importedCandidate,
      id: Date.now(),
      createdAt: new Date().toISOString()
    };

    const updatedCandidates = [...candidates, newCandidate];
    setCandidates(updatedCandidates);
    filterCandidates(searchQuery, tabValue);

    showSnackbar('Резюме кандидата успешно импортировано', 'success');
  };

  // Функция показа уведомления
  const showSnackbar = (message, severity = 'success') => {
    setSnackbar({
      open: true,
      message,
      severity
    });
  };

  // Закрытие уведомления
  const handleCloseSnackbar = () => {
    setSnackbar({
      ...snackbar,
      open: false
    });
  };

  const fetchResumes = async () => {
    try {
      const auth = localStorage.getItem('hr_partner_auth');
      const token = auth ? JSON.parse(auth).token : null;
      const response = await fetch('http://localhost:8081/api/resumes', {
        headers: {
          'Content-Type': 'application/json',
          ...(token ? { 'Authorization': `Bearer ${token}` } : {})
        }
      });
      if (!response.ok) throw new Error('Ошибка загрузки резюме');
      const data = await response.json();
      setCandidates(data);
      setFilteredCandidates(data);
    } catch (err) {
      showSnackbar(err.message || 'Ошибка загрузки резюме', 'error');
    }
  };

  useEffect(() => {
    fetchResumes();
    // eslint-disable-next-line
  }, []);

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="h4" component="h1">
          Кандидаты
        </Typography>

        <Box>
          <Button
            variant="outlined"
            startIcon={<ImportIcon />}
            onClick={handleImportMenuOpen}
            sx={{ mr: 2 }}
          >
            Импорт
          </Button>

          <Menu
            anchorEl={importMenuAnchorEl}
            open={Boolean(importMenuAnchorEl)}
            onClose={handleImportMenuClose}
          >
            <MenuItem onClick={handleOpenImportDialog}>
              <ListItemIcon>
                <TextIcon fontSize="small" />
              </ListItemIcon>
              <ListItemText>Импорт из текста</ListItemText>
            </MenuItem>
            <MenuItem onClick={handleOpenImportDialog}>
              <ListItemIcon>
                <FileIcon fontSize="small" />
              </ListItemIcon>
              <ListItemText>Импорт из файла</ListItemText>
            </MenuItem>
          </Menu>

          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={() => handleOpenForm()}
          >
            Добавить кандидата
          </Button>
        </Box>
      </Box>

      <Paper sx={{ mb: 3, p: 2 }}>
        <Grid container spacing={2}>
          <Grid item xs={12} md={8}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Поиск по имени, должности или навыкам..."
              value={searchQuery}
              onChange={handleSearch}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                )
              }}
            />
          </Grid>
          <Grid item xs={12} md={4}>
            <Button 
              variant="outlined" 
              fullWidth 
              startIcon={<FilterIcon />}
              onClick={() => console.log('Filter')}
            >
              Фильтры
            </Button>
          </Grid>
        </Grid>
      </Paper>

      <Paper sx={{ mb: 3 }}>
        <Tabs
          value={tabValue}
          onChange={handleTabChange}
          indicatorColor="primary"
          textColor="primary"
          variant="scrollable"
          scrollButtons="auto"
        >
          <Tab label={`Все (${candidates.length})`} />
          <Tab label={`Новые (${candidates.filter(c => c.status === 'new').length})`} />
          <Tab label={`На рассмотрении (${candidates.filter(c => c.status === 'reviewing').length})`} />
          <Tab label={`На собеседовании (${candidates.filter(c => c.status === 'interviewing').length})`} />
          <Tab label={`Предложение (${candidates.filter(c => c.status === 'offer').length})`} />
          <Tab label={`Отклоненные (${candidates.filter(c => c.status === 'rejected').length})`} />
        </Tabs>
        <Divider />

        <Box sx={{ p: 2 }}>
          {filteredCandidates.length > 0 ? (
            <Grid container spacing={3}>
              {filteredCandidates.map(candidate => (
                <Grid item xs={12} md={6} key={candidate.id}>
                  <CandidateCard 
                    candidate={candidate} 
                    onEdit={() => handleOpenForm(candidate)}
                    onDelete={handleDeleteCandidate}
                    onStatusChange={handleStatusChange}
                  />
                </Grid>
              ))}
            </Grid>
          ) : (
            <Box sx={{ textAlign: 'center', py: 4 }}>
              <Typography variant="h6" color="textSecondary">
                Кандидаты не найдены
              </Typography>
              <Typography variant="body2" color="textSecondary">
                Попробуйте изменить параметры поиска или добавить нового кандидата
              </Typography>
            </Box>
          )}
        </Box>
      </Paper>

      <Fab 
        color="primary" 
        sx={{ position: 'fixed', bottom: 16, right: 16 }}
        onClick={() => handleOpenForm()}
      >
        <AddIcon />
      </Fab>

      {/* Форма добавления/редактирования кандидата */}
      {formDialogOpen && (
        <CandidateForm
          open={formDialogOpen}
          onClose={handleCloseForm}
          candidate={currentCandidate}
        />
      )}

      {/* Диалог импорта резюме */}
      {importDialogOpen && (
        <CandidateImport
          open={importDialogOpen}
          onClose={handleCloseImportDialog}
          onImport={handleImportCandidate}
        />
      )}

      {/* Уведомления */}
      <Snackbar 
        open={snackbar.open} 
        autoHideDuration={4000} 
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default CandidateList;
