import React, { useState } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  Tabs,
  Tab,
  Box,
  Typography,
  Paper,
  IconButton,
  CircularProgress,
  Alert,
  Snackbar,
  Divider,
  Input,
  InputLabel,
  FormControl
} from '@mui/material';
import {
  ContentPaste as PasteIcon,
  Close as CloseIcon,
  Upload as UploadIcon,
  Description as FileIcon
} from '@mui/icons-material';

// Примеры форматов импорта
const sampleFormats = {
  text: `ФИО: Алексей Иванов
Должность: Frontend-разработчик
Местоположение: Москва
Зарплата: 150 000 ₽
Опыт работы: 4 года
Образование: Высшее, МГТУ им. Баумана
О себе: Опытный frontend-разработчик с опытом создания современных веб-приложений...
Навыки: React, JavaScript, TypeScript, HTML, CSS
Email: alexey@example.com
Телефон: +7 (900) 123-45-67
`
};

const CandidateImport = ({ open, onClose, onImport }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [textContent, setTextContent] = useState('');
  const [file, setFile] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  // Обработчик изменения вкладки
  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
    setError(null);
  };

  // Обработчик изменения текстового содержимого
  const handleTextChange = (e) => {
    setTextContent(e.target.value);
    if (error) setError(null);
  };

  // Обработчик изменения файла
  const handleFileChange = (e) => {
    if (e.target.files.length > 0) {
      setFile(e.target.files[0]);
      if (error) setError(null);
    }
  };

  // Обработчик вставки примера
  const handlePasteSample = () => {
    setTextContent(sampleFormats.text);
  };

  // Обработчик импорта
  const handleImport = async () => {
    setLoading(true);
    setError(null);

    try {
      if (activeTab === 0) { // Импорт из текста
        if (!textContent.trim()) {
          throw new Error('Пожалуйста, введите текст для импорта');
        }

        // Парсинг текста
        const parsedCandidate = parseTextToCandidate(textContent);

        // Имитация задержки запроса
        await new Promise(resolve => setTimeout(resolve, 1000));

        if (onImport) {
          onImport(parsedCandidate);
        }

        setSnackbarOpen(true);
        handleClose();
      } else { // Импорт из файла
        if (!file) {
          throw new Error('Пожалуйста, выберите файл для импорта');
        }

        // Проверка типа файла
        const allowedTypes = ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
        if (!allowedTypes.includes(file.type)) {
          throw new Error('Поддерживаются только файлы форматов PDF, DOC и DOCX');
        }

        // Имитация задержки запроса
        await new Promise(resolve => setTimeout(resolve, 1000));

        // Заглушка для импорта из файла (в реальном приложении здесь будет запрос к API для обработки файла)
        const mockCandidate = {
          name: file.name.split('.')[0],
          position: 'Должность не определена',
          location: 'Москва',
          experience: 'Не указано',
          salary: 'Не указано',
          education: 'Не указано',
          skills: [],
          about: `Импортировано из файла: ${file.name}`,
          email: 'email@example.com',
          phone: '',
          status: 'new',
          resumeUrl: '',
          rating: 3
        };

        if (onImport) {
          onImport(mockCandidate);
        }

        setSnackbarOpen(true);
        handleClose();
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Функция для парсинга текста в структуру кандидата
  const parseTextToCandidate = (text) => {
    const lines = text.split('\n');
    const candidate = {
      status: 'new',
      skills: [],
      rating: 3
    };

    lines.forEach(line => {
      const [key, value] = line.split(':').map(item => item.trim());
      if (!key || !value) return;

      switch (key.toLowerCase()) {
        case 'фио':
          candidate.name = value;
          break;
        case 'должность':
          candidate.position = value;
          break;
        case 'местоположение':
          candidate.location = value;
          break;
        case 'зарплата':
          candidate.salary = value;
          break;
        case 'опыт работы':
          candidate.experience = value;
          break;
        case 'образование':
          candidate.education = value;
          break;
        case 'о себе':
          candidate.about = value;
          break;
        case 'навыки':
          candidate.skills = value.split(',').map(skill => skill.trim());
          break;
        case 'email':
          candidate.email = value;
          break;
        case 'телефон':
          candidate.phone = value;
          break;
        default:
          break;
      }
    });

    // Проверка обязательных полей
    if (!candidate.name) {
      throw new Error('Не указано ФИО кандидата');
    }
    if (!candidate.position) {
      throw new Error('Не указана должность кандидата');
    }
    if (!candidate.email) {
      throw new Error('Не указан email кандидата');
    }

    return candidate;
  };

  // Обработчик закрытия диалога
  const handleClose = () => {
    setActiveTab(0);
    setTextContent('');
    setFile(null);
    setError(null);
    onClose();
  };

  // Обработчик закрытия снэкбара
  const handleSnackbarClose = () => {
    setSnackbarOpen(false);
  };

  return (
    <>
      <Dialog 
        open={open} 
        onClose={handleClose}
        fullWidth
        maxWidth="md"
      >
        <DialogTitle>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            Импортировать резюме кандидата
            <IconButton edge="end" onClick={handleClose}>
              <CloseIcon />
            </IconButton>
          </Box>
        </DialogTitle>
        <Divider />

        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
          <Tabs 
            value={activeTab} 
            onChange={handleTabChange} 
            variant="fullWidth"
            centered
          >
            <Tab label="Импорт из текста" />
            <Tab label="Импорт из файла" />
          </Tabs>
        </Box>

        <DialogContent>
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}

          {activeTab === 0 ? (
            <Box>
              <Typography variant="body2" sx={{ mb: 2 }}>
                Вставьте текст резюме в формате "Ключ: Значение" для каждой строки. Поддерживаемые ключи: ФИО, Должность, Местоположение, Зарплата, Опыт работы, Образование, О себе, Навыки, Email, Телефон.
              </Typography>

              <TextField
                fullWidth
                multiline
                rows={10}
                placeholder="Введите текст резюме..."
                value={textContent}
                onChange={handleTextChange}
                variant="outlined"
                disabled={loading}
              />

              <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 1 }}>
                <Button 
                  startIcon={<PasteIcon />} 
                  size="small" 
                  onClick={handlePasteSample}
                  disabled={loading}
                >
                  Вставить пример
                </Button>
              </Box>
            </Box>
          ) : (
            <Box>
              <Typography variant="body2" sx={{ mb: 2 }}>
                Загрузите файл резюме в формате PDF, DOC или DOCX. Система попытается автоматически извлечь информацию.
              </Typography>

              <Paper variant="outlined" sx={{ p: 3, textAlign: 'center', mb: 2 }}>
                <FormControl fullWidth>
                  <Input
                    id="resume-file-input"
                    type="file"
                    inputProps={{
                      accept: '.pdf,.doc,.docx,application/pdf,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document'
                    }}
                    onChange={handleFileChange}
                    sx={{ display: 'none' }}
                  />
                  <Box sx={{ textAlign: 'center', p: 3, border: '2px dashed #ccc', borderRadius: 1 }}>
                    <FileIcon sx={{ fontSize: 48, color: 'text.secondary', mb: 1 }} />
                    <Typography variant="body1" gutterBottom>
                      {file ? file.name : 'Перетащите файл сюда или нажмите для выбора'}
                    </Typography>
                    <Typography variant="caption" color="text.secondary" display="block" gutterBottom>
                      Поддерживаемые форматы: PDF, DOC, DOCX
                    </Typography>
                    <InputLabel htmlFor="resume-file-input">
                      <Button 
                        variant="contained" 
                        component="span"
                        startIcon={<UploadIcon />}
                        sx={{ mt: 2 }}
                      >
                        Выбрать файл
                      </Button>
                    </InputLabel>
                  </Box>
                </FormControl>
              </Paper>

              <Paper elevation={0} sx={{ p: 2, bgcolor: 'grey.100' }}>
                <Typography variant="caption" color="text.secondary">
                  Примечание: При импорте из файла возможны неточности в автоматическом извлечении информации. После импорта проверьте и отредактируйте данные при необходимости.
                </Typography>
              </Paper>
            </Box>
          )}
        </DialogContent>

        <DialogActions sx={{ px: 3, pb: 3 }}>
          <Button onClick={handleClose} color="inherit" disabled={loading}>
            Отмена
          </Button>
          <Button
            startIcon={loading ? <CircularProgress size={24} /> : <UploadIcon />}
            onClick={handleImport}
            variant="contained"
            color="primary"
            disabled={loading || (activeTab === 0 && !textContent.trim()) || (activeTab === 1 && !file)}
          >
            {loading ? 'Импортирую...' : 'Импортировать'}
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar
        open={snackbarOpen}
        autoHideDuration={4000}
        onClose={handleSnackbarClose}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }}
      >
        <Alert onClose={handleSnackbarClose} severity="success" sx={{ width: '100%' }}>
          Резюме кандидата успешно импортировано
        </Alert>
      </Snackbar>
    </>
  );
};

export default CandidateImport;
