import React, { useEffect, useState } from 'react';
import {
  Box,
  Typography,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  Chip,
  Button,
  CircularProgress,
  Alert
} from '@mui/material';
import {
  OpenInNew as OpenIcon,
  History as HistoryIcon
} from '@mui/icons-material';
import { useMatching } from '../../context/MatchingContext';
import { useNavigate } from 'react-router-dom';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';

// Функция для форматирования даты
const formatDate = (dateString) => {
  const date = new Date(dateString);
  return new Intl.DateTimeFormat('ru-RU', {
    day: '2-digit',
    month: '2-digit',
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  }).format(date);
};

// Функция для определения цвета статуса оценки
const getScoreColor = (score) => {
  if (score >= 0.7) return '#4caf50';
  if (score >= 0.5) return '#ff9800';
  return '#f44336';
};

// Функция для форматирования числа в процентный формат
const formatPercent = (num) => {
  return `${Math.round(num * 100)}%`;
};

const MatchingHistory = () => {
  const navigate = useNavigate();
  const { 
    matchHistory, 
    loading, 
    error, 
    fetchMatchHistory, 
    fetchMatchById
  } = useMatching();

  // Локальное состояние для пагинации
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  // Добавляю модальное окно для просмотра деталей сопоставления
  const [selectedMatch, setSelectedMatch] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);

  // Загрузка данных при монтировании компонента
  useEffect(() => {
    fetchMatchHistory({ page: page + 1, limit: rowsPerPage });
  }, [page, rowsPerPage, fetchMatchHistory]);

  // Обработчики изменения пагинации
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Обработчик просмотра детальной информации о сопоставлении
  const handleViewMatch = async (id) => {
    try {
      const match = matchHistory.find((m) => m.id === id);
      setSelectedMatch(match);
      setModalOpen(true);
    } catch (error) {
      console.error('Error fetching match details:', error);
    }
  };

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        История сопоставлений
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      <Paper>
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Резюме</TableCell>
                <TableCell>Вакансия</TableCell>
                <TableCell>Оценка</TableCell>
                <TableCell>Действия</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {loading ? (
                <TableRow>
                  <TableCell colSpan={5} align="center">
                    <CircularProgress size={30} sx={{ my: 2 }} />
                  </TableCell>
                </TableRow>
              ) : matchHistory.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={5} align="center">
                    <Box sx={{ py: 3, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                      <HistoryIcon sx={{ fontSize: 40, color: 'text.secondary', mb: 1 }} />
                      <Typography color="text.secondary">
                        История сопоставлений пуста
                      </Typography>
                    </Box>
                  </TableCell>
                </TableRow>
              ) : (
                matchHistory.map((match) => (
                  <TableRow key={match.id} hover>
                    <TableCell>{formatDate(match.createdAt)}</TableCell>
                    <TableCell>
                      {match.resumeName ? `${match.resumeName} (${match.resumeRole || ''})` : `ID: ${match.resumeId}`}
                    </TableCell>
                    <TableCell>
                      {match.vacancyTitle ? `${match.vacancyTitle} (${match.vacancyCompany || ''})` : `ID: ${match.vacancyId}`}
                    </TableCell>
                    <TableCell>
                      <Chip
                        label={formatPercent(match.score)}
                        sx={{ 
                          bgcolor: getScoreColor(match.score) + '20',
                          color: getScoreColor(match.score),
                          fontWeight: 'bold'
                        }}
                      />
                    </TableCell>
                    <TableCell>
                      <Button
                        variant="outlined"
                        size="small"
                        startIcon={<OpenIcon />}
                        onClick={() => handleViewMatch(match.id)}
                      >
                        Просмотр
                      </Button>
                    </TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </TableContainer>

        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={-1} // Сервер определяет общее количество
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
          labelRowsPerPage="Строк на странице:"
          labelDisplayedRows={({ from, to }) => `${from}-${to}`}
        />
      </Paper>

      {/* Модальное окно с деталями сопоставления */}
      <Dialog open={modalOpen} onClose={() => setModalOpen(false)} maxWidth="md" fullWidth>
        <DialogTitle>Детали сопоставления</DialogTitle>
        <DialogContent>
          {selectedMatch && (
            <Box>
              <Typography variant="subtitle1" sx={{ mb: 1 }}>
                <b>Дата:</b> {formatDate(selectedMatch.createdAt)}
              </Typography>
              <Typography variant="subtitle1" sx={{ mb: 1 }}>
                <b>Резюме:</b> {selectedMatch.resumeName ? `${selectedMatch.resumeName} (${selectedMatch.resumeRole || ''})` : `ID: ${selectedMatch.resumeId}`}
              </Typography>
              <Typography variant="subtitle1" sx={{ mb: 1 }}>
                <b>Вакансия:</b> {selectedMatch.vacancyTitle ? `${selectedMatch.vacancyTitle} (${selectedMatch.vacancyCompany || ''})` : `ID: ${selectedMatch.vacancyId}`}
              </Typography>
              <Typography variant="subtitle1" sx={{ mb: 1 }}>
                <b>Оценка:</b> {formatPercent(selectedMatch.score)}
              </Typography>
              <Typography variant="subtitle1" sx={{ mb: 1 }}>
                <b>Вердикт:</b> {selectedMatch.verdict}
              </Typography>
              <Typography variant="subtitle1" sx={{ mb: 1 }}>
                <b>Комментарий ИИ:</b> {selectedMatch.llmComment}
              </Typography>
              <Typography variant="subtitle1" sx={{ mb: 1 }}>
                <b>Совпавшие навыки:</b> {selectedMatch.matchedSkills && selectedMatch.matchedSkills.length > 0 ? selectedMatch.matchedSkills.join(', ') : '—'}
              </Typography>
              <Typography variant="subtitle1" sx={{ mb: 1 }}>
                <b>Не совпавшие навыки:</b> {selectedMatch.unmatchedSkills && selectedMatch.unmatchedSkills.length > 0 ? selectedMatch.unmatchedSkills.join(', ') : '—'}
              </Typography>
              <Typography variant="subtitle1" sx={{ mb: 1 }}>
                <b>Положительные стороны:</b>
              </Typography>
              <ul>
                {selectedMatch.positives && selectedMatch.positives.length > 0 ? selectedMatch.positives.map((pos, idx) => <li key={idx}>{pos}</li>) : <li>—</li>}
              </ul>
              <Typography variant="subtitle1" sx={{ mb: 1 }}>
                <b>Отрицательные стороны:</b>
              </Typography>
              <ul>
                {selectedMatch.negatives && selectedMatch.negatives.length > 0 ? selectedMatch.negatives.map((neg, idx) => <li key={idx}>{neg}</li>) : <li>—</li>}
              </ul>
              <Typography variant="subtitle1" sx={{ mb: 1 }}>
                <b>Уточняющие вопросы:</b>
              </Typography>
              <ul>
                {selectedMatch.clarifyingQuestions && selectedMatch.clarifyingQuestions.length > 0 ? selectedMatch.clarifyingQuestions.map((q, idx) => <li key={idx}>{q}</li>) : <li>—</li>}
              </ul>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setModalOpen(false)}>Закрыть</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default MatchingHistory;
