import React, { useState } from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Paper,
  Button,
  Grid,
  Tabs,
  Tab,
  Divider,
  TextField,
  InputAdornment,
  Fab,
  Snackbar,
  Alert,
  Menu,
  MenuItem,
  ListItemIcon,
  ListItemText
} from '@mui/material';
import { 
  Add as AddIcon, 
  Search as SearchIcon,
  FilterList as FilterIcon,
  CloudUpload as ImportIcon,
  Link as LinkIcon,
  TextSnippet as TextIcon
} from '@mui/icons-material';
import VacancyCard from './VacancyCard';
import VacancyForm from './VacancyForm';
import VacancyImport from './VacancyImport';

// Временные данные для вакансий
const mockVacancies = [
  { 
    id: 1, 
    title: 'Frontend-разработчик', 
    company: 'Tech Solutions', 
    location: 'Москва',
    salary: '120 000 - 180 000 ₽',
    experience: '3-5 лет',
    description: 'Мы ищем опытного Frontend-разработчика со знанием React и TypeScript...',
    skills: ['React', 'JavaScript', 'TypeScript', 'CSS', 'HTML'],
    createdAt: '2023-05-10T14:00:00Z',
    status: 'active'
  },
  { 
    id: 2, 
    title: 'UI/UX Дизайнер', 
    company: 'Creative Agency', 
    location: 'Удаленно',
    salary: '140 000 - 200 000 ₽',
    experience: '2-4 года',
    description: 'Ищем креативного дизайнера для создания пользовательских интерфейсов...',
    skills: ['Figma', 'Adobe XD', 'Sketch', 'Prototyping'],
    createdAt: '2023-05-12T10:00:00Z',
    status: 'active'
  },
  { 
    id: 3, 
    title: 'Java Developer', 
    company: 'FinTech Corp', 
    location: 'Санкт-Петербург',
    salary: '180 000 - 250 000 ₽',
    experience: '5+ лет',
    description: 'Требуется Java-разработчик для создания высоконагруженных систем...',
    skills: ['Java', 'Spring', 'Hibernate', 'SQL', 'Microservices'],
    createdAt: '2023-05-05T11:30:00Z',
    status: 'active'
  },
  { 
    id: 4, 
    title: 'Product Manager', 
    company: 'E-commerce Platform', 
    location: 'Москва',
    salary: '200 000 - 300 000 ₽',
    experience: '4-6 лет',
    description: 'Мы ищем опытного продакт-менеджера для развития нашей e-commerce платформы...',
    skills: ['Product Development', 'Agile', 'Market Analysis', 'User Research'],
    createdAt: '2023-05-01T09:15:00Z',
    status: 'closed'
  }
];

const VacancyList = () => {
  const [tabValue, setTabValue] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');
  const [vacancies, setVacancies] = useState(mockVacancies);
  const [filteredVacancies, setFilteredVacancies] = useState(mockVacancies);
  const [formDialogOpen, setFormDialogOpen] = useState(false);
  const [importDialogOpen, setImportDialogOpen] = useState(false);
  const [currentVacancy, setCurrentVacancy] = useState(null);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success'
  });
  const [importMenuAnchorEl, setImportMenuAnchorEl] = useState(null);

  // Обработчик изменения вкладки
  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
    filterVacancies(searchQuery, newValue);
  };

  // Обработчик поиска
  const handleSearch = (event) => {
    const query = event.target.value;
    setSearchQuery(query);
    filterVacancies(query, tabValue);
  };

  // Фильтрация вакансий
  const filterVacancies = (query, tab) => {
    let statusFilter = '';
    if (tab === 1) statusFilter = 'active';
    if (tab === 2) statusFilter = 'closed';

    setFilteredVacancies(
      vacancies.filter(vacancy => 
        (statusFilter ? vacancy.status === statusFilter : true) && 
        (vacancy.title.toLowerCase().includes(query.toLowerCase()) ||
        vacancy.skills.some(skill => skill.toLowerCase().includes(query.toLowerCase())))
      )
    );
  };

  // Обработчик открытия формы
  const handleOpenForm = (vacancy = null) => {
    setCurrentVacancy(vacancy);
    setFormDialogOpen(true);
  };

  // Обработчик закрытия формы
  const handleCloseForm = (formData) => {
    setFormDialogOpen(false);

    // Если пользователь отправил форму с данными
    if (formData && formData !== true) {
      if (currentVacancy) {
        // Редактирование существующей вакансии
        const updatedVacancies = vacancies.map(vac => 
          vac.id === currentVacancy.id ? { ...formData, id: currentVacancy.id, createdAt: currentVacancy.createdAt } : vac
        );
        setVacancies(updatedVacancies);
        filterVacancies(searchQuery, tabValue);

        showSnackbar('Вакансия успешно обновлена', 'success');
      } else {
        // Добавление новой вакансии
        const newVacancy = {
          ...formData,
          id: Date.now(), // Временный способ генерации ID
          createdAt: new Date().toISOString()
        };
        const updatedVacancies = [...vacancies, newVacancy];
        setVacancies(updatedVacancies);
        filterVacancies(searchQuery, tabValue);

        showSnackbar('Вакансия успешно создана', 'success');
      }
    }

    setCurrentVacancy(null);
  };

  // Обработчик удаления вакансии
  const handleDeleteVacancy = (id) => {
    const updatedVacancies = vacancies.filter(vacancy => vacancy.id !== id);
    setVacancies(updatedVacancies);
    filterVacancies(searchQuery, tabValue);

    showSnackbar('Вакансия удалена', 'success');
  };

  // Обработчик дублирования вакансии
  const handleDuplicateVacancy = (vacancy) => {
    const duplicateVacancy = {
      ...vacancy,
      id: Date.now(),
      title: `${vacancy.title} (копия)`,
      createdAt: new Date().toISOString()
    };

    const updatedVacancies = [...vacancies, duplicateVacancy];
    setVacancies(updatedVacancies);
    filterVacancies(searchQuery, tabValue);

    showSnackbar('Вакансия успешно дублирована', 'success');
  };

  // Обработчик изменения статуса вакансии
  const handleStatusChange = (id, newStatus) => {
    const updatedVacancies = vacancies.map(vacancy => 
      vacancy.id === id ? { ...vacancy, status: newStatus } : vacancy
    );

    setVacancies(updatedVacancies);
    filterVacancies(searchQuery, tabValue);

    const statusMessage = newStatus === 'active' ? 'Вакансия активирована' : 'Вакансия закрыта';
    showSnackbar(statusMessage, 'success');
  };

  // Обработчики импорта
  const handleImportMenuOpen = (event) => {
    setImportMenuAnchorEl(event.currentTarget);
  };

  const handleImportMenuClose = () => {
    setImportMenuAnchorEl(null);
  };

  const handleOpenImportDialog = () => {
    handleImportMenuClose();
    setImportDialogOpen(true);
  };

  const handleCloseImportDialog = () => {
    setImportDialogOpen(false);
  };

  const handleImportVacancy = (importedVacancy) => {
    const newVacancy = {
      ...importedVacancy,
      id: Date.now(),
      createdAt: new Date().toISOString()
    };

    const updatedVacancies = [...vacancies, newVacancy];
    setVacancies(updatedVacancies);
    filterVacancies(searchQuery, tabValue);

    showSnackbar('Вакансия успешно импортирована', 'success');
  };

  // Функция показа уведомления
  const showSnackbar = (message, severity = 'success') => {
    setSnackbar({
      open: true,
      message,
      severity
    });
  };

  // Закрытие уведомления
  const handleCloseSnackbar = () => {
    setSnackbar({
      ...snackbar,
      open: false
    });
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="h4" component="h1">
          Вакансии
        </Typography>

        <Box>
          <Button
            variant="outlined"
            startIcon={<ImportIcon />}
            onClick={handleImportMenuOpen}
            sx={{ mr: 2 }}
          >
            Импорт
          </Button>

          <Menu
            anchorEl={importMenuAnchorEl}
            open={Boolean(importMenuAnchorEl)}
            onClose={handleImportMenuClose}
          >
            <MenuItem onClick={handleOpenImportDialog}>
              <ListItemIcon>
                <TextIcon fontSize="small" />
              </ListItemIcon>
              <ListItemText>Импорт из текста</ListItemText>
            </MenuItem>
            <MenuItem onClick={handleOpenImportDialog}>
              <ListItemIcon>
                <LinkIcon fontSize="small" />
              </ListItemIcon>
              <ListItemText>Импорт по ссылке</ListItemText>
            </MenuItem>
          </Menu>

          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={() => handleOpenForm()}
          >
            Добавить вакансию
          </Button>
        </Box>
      </Box>

      <Paper sx={{ mb: 3, p: 2 }}>
        <Grid container spacing={2}>
          <Grid item xs={12} md={8}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Поиск по названию или навыкам..."
              value={searchQuery}
              onChange={handleSearch}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                )
              }}
            />
          </Grid>
          <Grid item xs={12} md={4}>
            <Button 
              variant="outlined" 
              fullWidth 
              startIcon={<FilterIcon />}
              onClick={() => console.log('Filter')}
            >
              Фильтры
            </Button>
          </Grid>
        </Grid>
      </Paper>

      <Paper sx={{ mb: 3 }}>
        <Tabs
          value={tabValue}
          onChange={handleTabChange}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
        >
          <Tab label={`Все (${vacancies.length})`} />
          <Tab label={`Активные (${vacancies.filter(v => v.status === 'active').length})`} />
          <Tab label={`Закрытые (${vacancies.filter(v => v.status === 'closed').length})`} />
        </Tabs>
        <Divider />

        <Box sx={{ p: 2 }}>
          {filteredVacancies.length > 0 ? (
            <Grid container spacing={3}>
              {filteredVacancies.map(vacancy => (
                <Grid item xs={12} md={6} key={vacancy.id}>
                  <VacancyCard 
                    vacancy={vacancy} 
                    onEdit={() => handleOpenForm(vacancy)}
                    onDelete={handleDeleteVacancy}
                    onDuplicate={handleDuplicateVacancy}
                    onStatusChange={handleStatusChange}
                  />
                </Grid>
              ))}
            </Grid>
          ) : (
            <Box sx={{ textAlign: 'center', py: 4 }}>
              <Typography variant="h6" color="textSecondary">
                Вакансии не найдены
              </Typography>
              <Typography variant="body2" color="textSecondary">
                Попробуйте изменить параметры поиска или создать новую вакансию
              </Typography>
            </Box>
          )}
        </Box>
      </Paper>

      <Fab 
        color="primary" 
        sx={{ position: 'fixed', bottom: 16, right: 16 }}
        onClick={() => handleOpenForm()}
      >
        <AddIcon />
      </Fab>

      {/* Форма добавления/редактирования вакансии */}
      <VacancyForm
        open={formDialogOpen}
        onClose={handleCloseForm}
        vacancy={currentVacancy}
      />

      {/* Диалог импорта вакансий */}
      <VacancyImport
        open={importDialogOpen}
        onClose={handleCloseImportDialog}
        onImport={handleImportVacancy}
      />

      {/* Уведомления */}
      <Snackbar 
        open={snackbar.open} 
        autoHideDuration={4000} 
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default VacancyList;
