import React from 'react';
import { 
  Container, 
  Grid, 
  Typography, 
  Paper, 
  Box 
} from '@mui/material';
import StatisticCard from './StatisticCard';
import { 
  WorkOutline as VacancyIcon, 
  PersonOutline as CandidateIcon,
  CheckCircleOutline as MatchIcon,
  TrendingUp as TrendIcon
} from '@mui/icons-material';
import { useAuth } from '../../context/AuthContext';

// Временные данные для статистики (в будущем будут загружаться с сервера)
const mockStatistics = {
  totalVacancies: 12,
  activeVacancies: 8,
  totalCandidates: 42,
  matchedCandidates: 15,
  interviewsScheduled: 7,
  recentMatches: 3
};

const Dashboard = () => {
  const { user } = useAuth();

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Добро пожаловать, {user?.name || 'пользователь'}!
        </Typography>
        <Typography variant="subtitle1" color="textSecondary">
          Панель управления HR Partner - ваш инструмент для эффективного подбора персонала
        </Typography>
      </Box>

      <Grid container spacing={3}>
        {/* Статистика по вакансиям */}
        <Grid item xs={12} sm={6} md={3}>
          <StatisticCard
            title="Всего вакансий"
            value={mockStatistics.totalVacancies}
            icon={<VacancyIcon />}
            color="#1976d2"
          />
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <StatisticCard
            title="Активные вакансии"
            value={mockStatistics.activeVacancies}
            icon={<VacancyIcon />}
            color="#2196f3"
          />
        </Grid>

        {/* Статистика по кандидатам */}
        <Grid item xs={12} sm={6} md={3}>
          <StatisticCard
            title="Всего кандидатов"
            value={mockStatistics.totalCandidates}
            icon={<CandidateIcon />}
            color="#4caf50"
          />
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <StatisticCard
            title="Назначено интервью"
            value={mockStatistics.interviewsScheduled}
            icon={<CandidateIcon />}
            color="#8bc34a"
          />
        </Grid>

        {/* Статистика по сопоставлениям */}
        <Grid item xs={12} sm={6} md={6}>
          <StatisticCard
            title="Кандидаты с высоким соответствием"
            value={mockStatistics.matchedCandidates}
            icon={<MatchIcon />}
            color="#ff9800"
            height={150}
          />
        </Grid>

        <Grid item xs={12} sm={6} md={6}>
          <StatisticCard
            title="Новые сопоставления за сегодня"
            value={mockStatistics.recentMatches}
            icon={<TrendIcon />}
            color="#f44336"
            height={150}
          />
        </Grid>

        {/* Последние активности */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Последние активности
            </Typography>
            <Box sx={{ p: 2, bgcolor: '#f5f5f5', borderRadius: 1 }}>
              <Typography variant="body2" sx={{ mb: 1 }}>
                Загружено новое резюме: "Менеджер по продажам" (15 минут назад)
              </Typography>
              <Typography variant="body2" sx={{ mb: 1 }}>
                Добавлена новая вакансия: "Frontend-разработчик" (2 часа назад)
              </Typography>
              <Typography variant="body2">
                Появилось 3 новых совпадения для вакансии "UX/UI дизайнер" (вчера)
              </Typography>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Dashboard;
